const SmallCard = ({ blog }) => {

  return (

      <div className="card">
        <section>
          <Link href={`/blogs/${blog.slug}`}>
            <a>
            <img
              className="img img-fluid"
              style={{ maxHeight: '150px', width: 'auto' }}
              src={`${API}/blog/photo/${blog.slug}`}
              alt={blog.title}
            />
