        <div className="body">
        <Link href={`/blogs/${blog.slug}`}>
          <a className="btn btn-primary pt-2">Read more</a>
        </Link>
        Posted {moment(blog.updatedAt).fromNow()} by {blog.postedBy.name}
        </div>
      </div>
  );
};

export default SmallCard;
